import React, { useState, useEffect } from 'react';
import { Send, Paperclip, Smile, MoreHorizontal, Phone, Video, Users, ChevronDown, Star, CheckCircle, Clock, Shield, Smartphone, Building2, CreditCard } from 'lucide-react';

const TeamsStitchDemo = () => {
  const [messages, setMessages] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);
  const [inputValue, setInputValue] = useState('');
  const [isTyping, setIsTyping] = useState(false);
  const [processingStep, setProcessingStep] = useState(0);
  const [isProcessing, setIsProcessing] = useState(false);

  const processingSteps = [
    "Validating employee details with Spliceforms HR portal...",
    "Verifying identity documents...",
    "Processing HDFC Bank account opening request...",
    "Configuring salary account settings...",
    "Finalizing account setup..."
  ];

  const initialMessage = {
    id: 1,
    sender: 'Copilot',
    isBot: true,
    timestamp: '9:15 AM',
    content: {
      type: 'welcome',
      text: "ðŸ‘‹ **Welcome to Spliceforms!** \n\nCongratulations on joining our team! To help with your onboarding, I can assist you in opening a **salary account with HDFC Bank** through our Stitch banking integration.\n\nâœ… **Quick & Easy Setup**\nâ€¢ Just ensure your HR portal has all personal details filled\nâ€¢ Instant account opening with zero paperwork\nâ€¢ Direct salary credits from day one\n\nWould you like me to help you open your HDFC salary account?",
      showButtons: true,
      buttons: [
        { text: "Yes, open HDFC account", action: "accept" },
        { text: "Maybe later", action: "decline" }
      ]
    }
  };

  useEffect(() => {
    // Add initial welcome message
    setTimeout(() => {
      setMessages([initialMessage]);
    }, 1000);
  }, []);

  const addMessage = (message) => {
    setMessages(prev => [...prev, { ...message, id: Date.now() }]);
  };

  const addTypingIndicator = () => {
    setIsTyping(true);
    setTimeout(() => {
      setIsTyping(false);
    }, 1500);
  };

  const handleButtonClick = (action) => {
    if (action === 'accept') {
      // User accepts
      addMessage({
        sender: 'You',
        isBot: false,
        timestamp: '9:16 AM',
        content: { type: 'text', text: 'Yes, open HDFC account' }
      });

      addTypingIndicator();
      setTimeout(() => {
        addMessage({
          sender: 'Copilot',
          isBot: true,
          timestamp: '9:16 AM',
          content: {
            type: 'terms',
            text: "ðŸ“‹ **Terms & Conditions - HDFC Bank Salary Account**\n\nPlease review and accept the following terms:\n\nâ€¢ Your salary account will be opened with HDFC Bank through Stitch middleware\nâ€¢ Account will be linked to Spliceforms for direct salary credits\nâ€¢ Standard HDFC Bank T&C apply for salary accounts\nâ€¢ Personal info from HR portal will be securely transmitted\nâ€¢ Account activation takes 24-48 hours\nâ€¢ Account details sent via SMS and email\n\nBy proceeding, you authorize Stitch to process your account opening with HDFC Bank.",
            showButtons: true,
            buttons: [
              { text: "âœ… Accept & Proceed", action: "proceed" },
              { text: "âŒ Cancel", action: "cancel" }
            ]
          }
        });
      }, 2000);
    } else if (action === 'proceed') {
      // User accepts terms
      addMessage({
        sender: 'You',
        isBot: false,
        timestamp: '9:17 AM',
        content: { type: 'text', text: 'âœ… Accept & Proceed' }
      });

      addTypingIndicator();
      setTimeout(() => {
        addMessage({
          sender: 'Copilot',
          isBot: true,
          timestamp: '9:17 AM',
          content: {
            type: 'processing',
            text: "ðŸ”„ **Processing your HDFC Bank account opening...**\n\nPlease wait while Stitch orchestrates your account setup:",
            showProcessing: true
          }
        });
        startProcessing();
      }, 2000);
    } else if (action === 'cancel' || action === 'decline') {
      addMessage({
        sender: 'You',
        isBot: false,
        timestamp: '9:16 AM',
        content: { type: 'text', text: action === 'cancel' ? 'âŒ Cancel' : 'Maybe later' }
      });

      addTypingIndicator();
      setTimeout(() => {
        addMessage({
          sender: 'Copilot',
          isBot: true,
          timestamp: '9:16 AM',
          content: {
            type: 'text',
            text: "No problem! Feel free to reach out whenever you're ready to open your salary account. I'm here to help! ðŸ˜Š",
            showButtons: true,
            buttons: [{ text: "ðŸ”„ Start Over", action: "restart" }]
          }
        });
      }, 1500);
    } else if (action === 'restart') {
      setMessages([]);
      setTimeout(() => {
        setMessages([initialMessage]);
      }, 500);
    }
  };

  const startProcessing = () => {
    setIsProcessing(true);
    setProcessingStep(0);
  };

  useEffect(() => {
    if (isProcessing) {
      const interval = setInterval(() => {
        setProcessingStep(prev => {
          if (prev < processingSteps.length - 1) {
            return prev + 1;
          } else {
            setIsProcessing(false);
            // Add success message
            setTimeout(() => {
              addMessage({
                sender: 'Copilot',
                isBot: true,
                timestamp: '9:18 AM',
                content: {
                  type: 'success',
                  text: "ðŸŽ‰ **Account Created Successfully!**\n\nYour HDFC Bank salary account has been opened successfully!\n\nðŸ’³ **Account Details:**\nâ€¢ Account Number: ****-****-7892\nâ€¢ IFSC Code: HDFC0001234\nâ€¢ Account Type: Salary Account\nâ€¢ Branch: Bangalore Corporate\n\nðŸ“± **Next Steps:**\nComplete account details and debit card information will be sent to your registered mobile number **+91-****-***-567** within the next few minutes.\n\nâœ¨ *Powered by Stitch Banking Middleware*",
                  showButtons: true,
                  buttons: [{ text: "ðŸ”„ Start New Demo", action: "restart" }]
                }
              });
            }, 1000);
            return prev;
          }
        });
      }, 1200);
      return () => clearInterval(interval);
    }
  }, [isProcessing]);

  const handleSendMessage = () => {
    if (inputValue.trim()) {
      addMessage({
        sender: 'You',
        isBot: false,
        timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
        content: { type: 'text', text: inputValue }
      });
      setInputValue('');

      // Simple bot response for custom messages
      addTypingIndicator();
      setTimeout(() => {
        addMessage({
          sender: 'Copilot',
          isBot: true,
          timestamp: new Date().toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'}),
          content: { 
            type: 'text', 
            text: "I'm here to help with your HDFC Bank salary account opening. Please use the buttons above to continue with the process! ðŸ˜Š" 
          }
        });
      }, 1500);
    }
  };

  const renderProcessingStatus = () => {
    return (
      <div className="mt-3 space-y-2">
        {processingSteps.map((step, index) => (
          <div
            key={index}
            className={`flex items-center gap-2 text-xs p-2 rounded ${
              index < processingStep
                ? 'bg-green-50 text-green-800'
                : index === processingStep
                ? 'bg-blue-50 text-blue-800'
                : 'bg-gray-50 text-gray-500'
            }`}
          >
            {index < processingStep ? (
              <CheckCircle className="w-3 h-3 text-green-600" />
            ) : index === processingStep ? (
              <div className="w-3 h-3 border border-blue-600 border-t-transparent rounded-full animate-spin" />
            ) : (
              <div className="w-3 h-3 border border-gray-300 rounded-full" />
            )}
            <span>{step}</span>
          </div>
        ))}
      </div>
    );
  };

  const renderMessage = (message) => {
    const isBot = message.isBot;

    return (
      <div key={message.id} className={`flex gap-3 mb-4 ${isBot ? '' : 'flex-row-reverse'}`}>
        <div className={`w-8 h-8 rounded-full flex items-center justify-center flex-shrink-0 ${
          isBot ? 'bg-purple-100' : 'bg-blue-100'
        }`}>
          {isBot ? (
            <span className="text-purple-600 text-sm font-bold">AI</span>
          ) : (
            <span className="text-blue-600 text-sm font-bold">You</span>
          )}
        </div>

        <div className={`max-w-md ${isBot ? '' : 'text-right'}`}>
          <div className={`inline-block p-3 rounded-lg ${
            isBot 
              ? 'bg-gray-100 text-gray-800' 
              : 'bg-blue-600 text-white'
          }`}>
            <div className="whitespace-pre-line text-sm">
              {message.content.text}
            </div>

            {message.content.showProcessing && renderProcessingStatus()}

            {message.content.showButtons && (
              <div className="mt-3 space-y-2">
                {message.content.buttons.map((button, index) => (
                  <button
                    key={index}
                    onClick={() => handleButtonClick(button.action)}
                    className="block w-full bg-blue-600 hover:bg-blue-700 text-white text-xs py-2 px-3 rounded transition-colors"
                  >
                    {button.text}
                  </button>
                ))}
              </div>
            )}
          </div>

          <div className={`text-xs text-gray-500 mt-1 ${isBot ? 'text-left' : 'text-right'}`}>
            <span className="font-medium">{message.sender}</span> â€¢ {message.timestamp}
          </div>
        </div>
      </div>
    );
  };

  return (
    <div className="h-screen bg-white flex flex-col max-w-4xl mx-auto border-x border-gray-200">
      {/* Teams Header */}
      <div className="bg-white border-b border-gray-200 p-4 flex items-center justify-between">
        <div className="flex items-center gap-3">
          <div className="w-8 h-8 bg-purple-600 rounded flex items-center justify-center">
            <span className="text-white text-sm font-bold">AI</span>
          </div>
          <div>
            <h1 className="font-semibold text-gray-900">Copilot</h1>
            <p className="text-xs text-gray-500">AI Assistant â€¢ Stitch Banking Integration</p>
          </div>
        </div>

        <div className="flex items-center gap-2">
          <button className="p-2 hover:bg-gray-100 rounded">
            <Phone className="w-4 h-4 text-gray-600" />
          </button>
          <button className="p-2 hover:bg-gray-100 rounded">
            <Video className="w-4 h-4 text-gray-600" />
          </button>
          <button className="p-2 hover:bg-gray-100 rounded">
            <Users className="w-4 h-4 text-gray-600" />
          </button>
          <button className="p-2 hover:bg-gray-100 rounded">
            <MoreHorizontal className="w-4 h-4 text-gray-600" />
          </button>
        </div>
      </div>

      {/* Chat Messages */}
      <div className="flex-1 overflow-y-auto p-4 space-y-4">
        {messages.map(renderMessage)}

        {isTyping && (
          <div className="flex gap-3 mb-4">
            <div className="w-8 h-8 rounded-full bg-purple-100 flex items-center justify-center flex-shrink-0">
              <span className="text-purple-600 text-sm font-bold">AI</span>
            </div>
            <div className="bg-gray-100 rounded-lg p-3">
              <div className="flex gap-1">
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce"></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                <div className="w-2 h-2 bg-gray-400 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* Message Input */}
      <div className="border-t border-gray-200 p-4">
        <div className="flex items-center gap-2 bg-gray-50 rounded-lg border border-gray-200 p-2">
          <button className="p-2 hover:bg-gray-200 rounded">
            <Paperclip className="w-4 h-4 text-gray-600" />
          </button>

          <input
            type="text"
            placeholder="Type a message..."
            value={inputValue}
            onChange={(e) => setInputValue(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && handleSendMessage()}
            className="flex-1 bg-transparent outline-none text-sm text-gray-900 placeholder-gray-500"
          />

          <button className="p-2 hover:bg-gray-200 rounded">
            <Smile className="w-4 h-4 text-gray-600" />
          </button>

          <button
            onClick={handleSendMessage}
            className="p-2 bg-blue-600 hover:bg-blue-700 rounded text-white transition-colors"
          >
            <Send className="w-4 h-4" />
          </button>
        </div>

        <p className="text-xs text-gray-500 mt-2 text-center">
          Copilot can make mistakes. Consider checking important information.
        </p>
      </div>
    </div>
  );
};

export default TeamsStitchDemo;
